    return (
      <div className='RollDice'>
        <div className='RollDice-container'>
          <Die face={die1} rolling={rolling} />
          <Die face={die2} rolling={rolling} />
        </div>
        <button onClick={this.roll} disabled={rolling}>
          {rolling ? "Rolling..." : "Roll Dice!"}
        </button>
      </div>
    );

}
